import React, { useEffect, useMemo, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import M from 'materialize-css';

const pad2 = (n) => String(n).padStart(2, '0');

const toTimeInput = (stime) => {
  // stime like 930 -> '09:30'
  const hr = Math.floor(stime / 100);
  const min = stime % 100;
  return `${pad2(hr)}:${pad2(min)}`;
};

export default function Details(props) {
  const navigate = useNavigate();

  const today = useMemo(() => new Date().toISOString().split('T')[0], []);

  const [title, setTitle] = useState('');
  const [email1, setEmail1] = useState('');
  const [email2, setEmail2] = useState('');
  const [date, setDate] = useState('');
  const [startTime, setStartTime] = useState('');
  const [endTime, setEndTime] = useState('');

  useEffect(() => {
    if (props.func === 'edit') {
      (async () => {
        try {
          const res = await fetch(`/meetingDetail/${props.meetingId}`);
          if (!res.ok) throw new Error('Failed to fetch meeting');
          const payload = await res.json();
          const data = payload.meeting;
          setTitle(data.title || '');
          setEmail1(data.user1?.email || '');
          setEmail2(data.user2?.email || '');
          setDate(data.date || '');
          setStartTime(toTimeInput(data.startTime));
          setEndTime(toTimeInput(data.endTime));
        } catch (err) {
          console.error(err);
          M.toast({ html: 'Unable to load meeting', classes: '#d32f2f red darken-2' });
        }
      })();
    }
  }, [props.func, props.meetingId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      let path = `/${props.func}`;
      if (props.meetingId !== undefined) path += `/${props.meetingId}`;

      const res = await fetch(path, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ title, date, startTime, endTime, email1, email2 }),
      });

      const data = await res.json();
      if (!res.ok || data.error) {
        const msg = data?.error || 'Save failed';
        M.toast({ html: msg, classes: '#d32f2f red darken-2' });
        return;
      }
      M.toast({ html: 'Saved successfully', classes: '#43a047 green darken-1' });
      navigate('/');
    } catch (err) {
      console.error(err);
      M.toast({ html: 'Network error', classes: '#d32f2f red darken-2' });
    }
  };

  return (
    <div>
      <h3>Fill form</h3>
      <div className="row" style={{ width: 500, padding: '20px', border: '1px solid lightgray' }}>
        <form className="col s12" onSubmit={handleSubmit}>
          <div className="row">
            <div className="input-field col s12">
              <input
                placeholder="Meeting Title"
                id="title"
                type="text"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                required
              />
            </div>
          </div>

          <div className="row">
            <div className="input-field col s6">
              <input
                id="email1"
                type="email"
                placeholder="Email 1"
                value={email1}
                onChange={(e) => setEmail1(e.target.value)}
                required
              />
            </div>
            <div className="input-field col s6">
              <input
                id="email2"
                type="email"
                placeholder="Email 2"
                value={email2}
                onChange={(e) => setEmail2(e.target.value)}
                required
              />
            </div>
          </div>

          <div className="row">
            <div className="input-field col s12">
              <input
                name="date"
                type="date"
                min={today}
                value={date}
                onChange={(e) => setDate(e.target.value)}
                required
              />
            </div>
          </div>

          <div className="row">
            <div className="input-field col s6">
              <label htmlFor="start-time" style={{ display: 'block' }}>Start time</label>
              <input
                id="start-time"
                type="time"
                name="start-time"
                value={startTime}
                onChange={(e) => setStartTime(e.target.value)}
                required
              />
            </div>
            <div className="input-field col s6">
              <label htmlFor="end-time" style={{ display: 'block' }}>End time</label>
              <input
                id="end-time"
                type="time"
                name="end-time"
                value={endTime}
                onChange={(e) => setEndTime(e.target.value)}
                required
              />
            </div>
          </div>

          <button type="submit" className="btn">Save</button>
        </form>
      </div>
    </div>
  );
}
